import React, { useState, useEffect } from 'react';

const API_URL = process.env.REACT_APP_API_URL;

// Este componente recibe el 'token' y 'onLogout' desde App.js
function ExpenseManager({ token, onLogout }) {
  // --- Estados de Gastos (Existentes)
  const [expenses, setExpenses] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('Varios');
  const [error, setError] = useState(null);

  // --- NUEVO: Estados de Ingresos ---
  const [incomes, setIncomes] = useState([]);
  const [incomeDescription, setIncomeDescription] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');
  // --- FIN NUEVO ---


  // --- Funci√≥n de Fetch con Auth (Existente)
  const authedFetch = (url, options = {}) => {
    const headers = {
      ...options.headers,
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}` // Incluimos el token
    };
    return fetch(url, { ...options, headers });
  };
  // ==========================

  // --- Cargar Gastos (Existente)
  const fetchExpenses = () => {
    authedFetch(`${API_URL}/expenses`) // Usamos authedFetch
      .then((res) => {
        if (res.status === 401 || res.status === 403) onLogout(); // Token inv√°lido, desloguear
        return res.json();
      })
      .then((data) => {
        if (data.error) throw new Error(data.error);
        setExpenses(data);
      })
      .catch((err) => {
        console.error(err);
        setError('Error al cargar los gastos.');
      });
  };

  // --- NUEVO: Cargar Ingresos ---
  const fetchIncomes = () => {
    authedFetch(`${API_URL}/incomes`)
      .then((res) => {
        if (res.status === 401 || res.status === 403) onLogout();
        return res.json();
      })
      .then((data) => {
        if (data.error) throw new Error(data.error);
        setIncomes(data);
      })
      .catch((err) => {
        console.error(err);
        setError('Error al cargar los ingresos.');
      });
  };
  // --- FIN NUEVO ---


  // Efecto inicial para cargar datos
  useEffect(() => {
    if (token) {
      fetchExpenses();
      fetchIncomes(); // NUEVO: Cargar ingresos tambi√©n
    }
  }, [token]); // El array de dependencias est√° bien

  // --- Manejador para crear Gasto (Existente)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!description || !amount) {
      alert('Descripci√≥n y Monto son obligatorios');
      return;
    }
    try {
      const response = await authedFetch(`${API_URL}/expenses`, {
        method: 'POST',
        body: JSON.stringify({ description, amount: parseFloat(amount), category }),
      });
      if (!response.ok) throw new Error('Error al guardar el gasto');
      setDescription('');
      setAmount('');
      setCategory('Varios');
      setError(null);
      fetchExpenses(); // Recargar gastos
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  // --- NUEVO: Manejador para crear Ingreso ---
  const handleIncomeSubmit = async (e) => {
    e.preventDefault();
    if (!incomeDescription || !incomeAmount) {
      alert('Descripci√≥n y Monto son obligatorios');
      return;
    }
    try {
      const response = await authedFetch(`${API_URL}/incomes`, {
        method: 'POST',
        body: JSON.stringify({ description: incomeDescription, amount: parseFloat(incomeAmount) }),
      });
      if (!response.ok) throw new Error('Error al guardar el ingreso');
      setIncomeDescription('');
      setIncomeAmount('');
      setError(null);
      fetchIncomes(); // Recargar ingresos
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };
  // --- FIN NUEVO ---

  // --- Manejador para borrar Gasto (Existente)
  const handleDelete = async (id) => {
    if (!window.confirm('¬øSeguro que quieres borrar este gasto?')) {
      return;
    }
    try {
      const response = await authedFetch(`${API_URL}/expenses/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Error al borrar el gasto');
      setExpenses(expenses.filter(ex => ex.id !== id)); 
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };
  
  // --- NUEVO: Manejador para borrar Ingreso ---
  const handleIncomeDelete = async (id) => {
    if (!window.confirm('¬øSeguro que quieres borrar este ingreso?')) {
      return;
    }
    try {
      const response = await authedFetch(`${API_URL}/incomes/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Error al borrar el ingreso');
      setIncomes(incomes.filter(inc => inc.id !== id)); // Actualizar estado de ingresos
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };
  // --- FIN NUEVO ---

  // Duplicar Gasto... (Existente)
  const handleDuplicate = async (expenseToDuplicate) => {
    if (!window.confirm('¬øDuplicar este gasto?')) return;
    const { description, amount, category } = expenseToDuplicate;
    try {
      const response = await authedFetch(`${API_URL}/expenses`, {
        method: 'POST',
        body: JSON.stringify({ description, amount: parseFloat(amount), category }),
      });
      if (!response.ok) throw new Error('Error al duplicar el gasto');
      setError(null);
      fetchExpenses();
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };


  // --- C√ÅLCULOS DE TOTALES (Modificado) ---
  const totalExpenses = expenses.reduce(
    (sum, expense) => sum + parseFloat(expense.amount), 
    0
  );
  
  // NUEVO
  const totalIncomes = incomes.reduce(
    (sum, income) => sum + parseFloat(income.amount),
    0
  );

  // NUEVO
  const balance = totalIncomes - totalExpenses;
  // --- FIN C√ÅLCULOS ---


  return (
    <> {/* Usamos Fragment para no a√±adir un div extra */}
      <header>
        <h1>Mi Gestor de Gastos üí∏</h1>
        <button onClick={onLogout} className="logout-btn">Cerrar Sesi√≥n</button>
      </header>

      {error && <div className="error-box">{error}</div>}

      {/* =========================================== */}
      {/* ==    NUEVA: BARRA DE BALANCE            == */}
      {/* =========================================== */}
      <div className="balance-bar">
        <div className="balance-card income">
          <span className="balance-label">Ingresos Totales</span>
          <span className="balance-amount">${totalIncomes.toFixed(2)}</span>
        </div>
        <div className="balance-card expense">
          <span className="balance-label">Gastos Totales</span>
          <span className="balance-amount">-${totalExpenses.toFixed(2)}</span>
        </div>
        <div className="balance-card balance">
          <span className="balance-label">Balance General</span>
          <span className="balance-amount" style={{ color: balance >= 0 ? 'green' : 'red' }}>
            ${balance.toFixed(2)}
          </span>
        </div>
      </div>
      {/* =========================================== */}
      {/* ==    FIN DE BARRA DE BALANCE            == */}
      {/* =========================================== */}


      <div className="content">
        
        {/* Columna de Formularios */}
        <div className="forms-column">

          {/* =========================================== */}
          {/* ==    NUEVO: FORMULARIO DE INGRESOS      == */}
          {/* =========================================== */}
          <form onSubmit={handleIncomeSubmit} className="income-form">
            <h2>Agregar Nuevo Ingreso</h2>
            <div className="form-group">
              <label>Descripci√≥n*</label>
              <input
                type="text"
                value={incomeDescription}
                onChange={(e) => setIncomeDescription(e.target.value)}
                placeholder="Ej: Salario"
              />
            </div>
            <div className="form-group">
              <label>Monto ($)*</label>
              <input
                type="number"
                step="0.01"
                value={incomeAmount}
                onChange={(e) => setIncomeAmount(e.target.value)}
                placeholder="Ej: 500.00"
              />
            </div>
            <button type="submit">Guardar Ingreso</button>
          </form>
          {/* =========================================== */}
          {/* ==    FIN DE FORMULARIO DE INGRESOS      == */}
          {/* =========================================== */}


          {/* Formulario de Gastos (Existente) */}
          <form onSubmit={handleSubmit} className="expense-form">
            <h2>Agregar Nuevo Gasto</h2>
            {/* ... (Tu formulario de gastos sigue igual) ... */}
            <div className="form-group">
              <label>Descripci√≥n*</label>
              <input
                type="text"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Ej: Almuerzo"
              />
            </div>
            <div className="form-group">
              <label>Monto ($)*</label>
              <input
                type="number"
                step="0.01"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Ej: 10.50"
              />
            </div>
            <div className="form-group">
              <label>Categor√≠a</label>
              <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="Varios">Varios</option>
                <option value="Comida">Comida</option>
                <option valueT="Transporte">Transporte</option>
                <option value="Servicios">Servicios</option>
                <option value="Entretenimiento">Entretenimiento</option>
              </select>
            </div>
            <button type="submit">Guardar Gasto</button>
          </form>
        </div>


        {/* Columna de Listas */}
        <div className="lists-column">

          {/* =========================================== */}
          {/* ==     NUEVA: LISTA DE INGRESOS          == */}
          {/* =========================================== */}
          <div className="income-list">
            <h2>Ingresos Registrados</h2>
            {incomes.length === 0 ? (
              <p>No hay ingresos. ¬°Agrega el primero!</p>
            ) : (
              incomes.map((income) => (
                <article key={income.id} className="income-card">
                  <div className="income-info">
                    <h3>{income.description}</h3>
                  </div>
                  <div className="income-amount">
                    <span>+${parseFloat(income.amount).toFixed(2)}</span>
                    <button 
                      className="delete-btn"
                      title="Eliminar ingreso"
                      onClick={() => handleIncomeDelete(income.id)}>
                      &times;
                    </button>
                  </div>
                </article>
              ))
            )}
          </div>
          {/* =========================================== */}
          {/* ==      FIN DE LISTA DE INGRESOS         == */}
          {/* =========================================== */}


          {/* Lista de Gastos (Existente) */}
          <div className="expense-list">
            <h2>Gastos Registrados</h2>
            
            <div className="total-display">
              Total Gastado: <span>${totalExpenses.toFixed(2)}</span>
            </div>

            {expenses.length === 0 ? (
              <p>No hay gastos. ¬°Agrega el primero!</p>
            ) : (
              expenses.map((expense) => (
                <article key={expense.id} className="expense-card">
                  <div className="expense-info">
                    <span className="category-tag">[{expense.category || 'Varios'}]</span>
                    <h3>{expense.description}</h3>
                  </div>
                  <div className="expense-amount">
                    <span>-${parseFloat(expense.amount).toFixed(2)}</span> {/* Cambiado a negativo para consistencia */}
                    <button 
                      className="duplicate-btn"
                      title="Duplicar gasto"
                      onClick={() => handleDuplicate(expense)}>
                      +
                    </button>
                    <button 
                      className="delete-btn"
                      title="Eliminar gasto"
                      onClick={() => handleDelete(expense.id)}>
                      &times;
                    </button>
                  </div>
                </article>
              ))
            )}
          </div>
        </div>

      </div>
    </>
  );
}

export default ExpenseManager;
