import React, { useState, useEffect } from 'react';
import './App.css'; // Importaremos un CSS simple

// La URL de la API viene de la variable de entorno
const API_URL = process.env.REACT_APP_API_URL;

function App() {
  const [recipes, setRecipes] = useState([]);
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [error, setError] = useState(null);

  // Cargar recetas al iniciar
  const fetchRecipes = () => {
    fetch(`${API_URL}/recipes`)
      .then((res) => res.json())
      .then((data) => setRecipes(data))
      .catch((err) => {
        console.error(err);
        setError('Error al cargar las recetas. ¬øEst√° el backend funcionando?');
      });
  };

  useEffect(() => {
    fetchRecipes();
  }, []);

  // Manejador para enviar el formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !instructions) {
      alert('T√≠tulo e Instrucciones son obligatorios');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/recipes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, ingredients, instructions }),
      });

      if (!response.ok) {
        throw new Error('Error al guardar la receta');
      }

      // Limpiar formulario
      setTitle('');
      setIngredients('');
      setInstructions('');
      setError(null);

      // Volver a cargar las recetas
      fetchRecipes();
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  // Manejador para borrar una receta
  const handleDelete = async (id) => {
    if (!window.confirm('¬øSeguro que quieres borrar esta receta?')) {
      return;
    }

    try {
      const response = await fetch(`${API_URL}/recipes/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Error al borrar la receta');
      }
      // Actualizar la lista de recetas filtrando la eliminada
      setRecipes(recipes.filter(recipe => recipe.id !== id));
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };


  return (
    <div className="container">
      <header>
        <h1>Mi Libro de Recetas üç≤</h1>
      </header>

      {error && <div className="error-box">{error}</div>}

      <div className="content">
        <form onSubmit={handleSubmit} className="recipe-form">
          <h2>Agregar Nueva Receta</h2>
          <div className="form-group">
            <label>T√≠tulo*</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Ej: Tacos al Pastor"
            />
          </div>
          <div className="form-group">
            <label>Ingredientes</label>
            <input
              type="text"
              value={ingredients}
              onChange={(e) => setIngredients(e.target.value)}
              placeholder="Ej: Tortillas, Cerdo, Pi√±a, Cilantro..."
            />
          </div>
          <div className="form-group">
            <label>Instrucciones*</label>
            <textarea
              value={instructions}
              onChange={(e) => setInstructions(e.target.value)}
              placeholder="1. Marinar la carne..."
            />
          </div>
          <button type="submit">Guardar Receta</button>
        </form>

        <div className="recipe-list">
          <h2>Recetas Guardadas</h2>
          {recipes.length === 0 ? (
            <p>No hay recetas. ¬°Agrega la primera!</p>
          ) : (
            recipes.map((recipe) => (
              <article key={recipe.id} className="recipe-card">
                <h3>{recipe.title}</h3>
                {recipe.ingredients && (
                  <>
                    <strong>Ingredientes:</strong>
                    <p>{recipe.ingredients}</p>
                  </>
                )}
                <strong>Instrucciones:</strong>
                <p>{recipe.instructions}</p>
                <button 
                  className="delete-btn"
                  onClick={() => handleDelete(recipe.id)}>
                  Borrar
                </button>
              </article>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
